import React from 'react';
import ReactDOM from 'react-dom';
import './ProdCard.css'

class ProdCard extends React.Component {
  constructor(props) {
       super(props);
       this.state = {
           showProCat: 0,
         };
  }
  render() {
    var showPro = 0 ;
    //all categories are checked
    if(this.props.isNail && this.props.isFashion && this.props.isBeauty){
      showPro = 1;
    }
    //nails and Fashion checked
    else if (this.props.isNail && this.props.isFashion && !this.props.isBeauty){
      showPro = 2;
    //only nails
    }else if (this.props.isNail && !this.props.isFashion && !this.props.isBeauty){
      showPro = 3;
    //nails and beauty checked
    }else if (this.props.isNail && !this.props.isFashion && this.props.isBeauty) {
      showPro = 4;
    //beauty and Fashion checked
    }else if (!this.props.isNail && this.props.isFashion && this.props.isBeauty) {
      showPro = 5;
      //only beauty
    }else if (!this.props.isNail && !this.props.isFashion && this.props.isBeauty) {
      showPro = 6;
      //only fashion
    }else if (!this.props.isNail && this.props.isFashion && !this.props.isBeauty) {
      showPro = 7;
    }
    this.setState({showProCat: showPro})


    return (
      (this.state.showPro == 1 || this.state.showPro == 0)?
      (
        <div className="card col-md-3 m-3 prodCard text-center">
          <img className="card-img-top prodImg" src={this.props.item.imgSrc} alt={this.props.item.imgAlt} />
          <div className="card-body">
            <h4 className="card-title">{this.props.item.prodName}</h4>
            <p className="card-text">{this.props.item.desc}</p>
            <a href='/cart' className='btn btn-secondary stretched-link'>הוספה לסל</a>
          </div>
        </div>
      ):
      (//only Nails checked
        (this.state.showPro == 1) ?
        (
          (this.props.item.category == 1)?
          (
            <div className="card col-md-3 m-3 prodCard text-center">
              <img className="card-img-top prodImg" src={this.props.item.imgSrc} alt={this.props.item.imgAlt} />
              <div className="card-body">
                <h4 className="card-title">{this.props.item.prodName}</h4>
                <p className="card-text">{this.props.item.desc}</p>
                <a href='/cart' className='btn btn-secondary stretched-link'>הוספה לסל</a>
              </div>
            </div>
          ):
          (
            <div>
              CHoose category
            </div>
          )
        ):
        (
          <div>
            CHoose category
          </div>
        )

      )
    )
  }
}

export default ProdCard;
