import React from 'react';
import ReactDOM from 'react-dom';
import './Cart.css'

class Cart extends React.Component {
  constructor(props)
  {
    super(props);
    this.state = {
      value: 1,
    }
    this.ref = React.createRef();
  }


  addFunction = () => {
      let val = this.state.value;
      val += 1;
      this.setState({value: val})
    ;
  };
  subFunction = () => {
    document.getElementById("demo").innerHTML = "Hello World";
  }
  render() {
    return (
      <div className="shopping-cart text-center">
        <h3 className='title1'>
          שלום רב {this.props.user.name} זהו סל המוצרים שלך
        </h3>

        <div className="item">
          <div className="buttons">
            <span className="delete-btn"></span>
          </div>

          <div className="image">
            <img className = 'imageStyle' src='/img/fashion-facts.jpg' alt=""/>
          </div>

          <div className="description">
            <span>Maison Margiela sddasdad</span>
          </div>

          <div className="quantity">
            <button
              className="plus-btn"
              type="button"
              name="button"
              onclick={() => this.addFunction('+')}>
              +
            </button>

            <input
              type="text"
              name="name"
              value={this.state.value}
              ref={this.ref}/>

            <button
              className="minus-btn"
              type="button"
              name="button"
              onclick={this.subFunction.bind(this)}>
              -
            </button>
          </div>

          <div className="total-price">$870</div>
        </div>

      </div>
    );
  }
}

export default Cart;
