import React from 'react';
import ReactDOM from 'react-dom';
import './Cart.css'

class Cart extends React.Component {
  constructor(props)
  {
    super(props);
    this.state = {
      value: 1,
    }
    this.ref = React.createRef();
  }

  addFunction = (val) => {
    this.setState({value: this.state.value+ 1})
      console.log(val);
  };
  subFunction = (val) => {
    this.setState({value: this.state.value- 1})
    (this.state.value == 0)?
      this.setState({value: 0}):
      console.log(val);
  }
  render() {
    var productsForUser = [];
    for(var j=0; j<this.props.user.cart; j++){
      for (var i=0 ; i < this.props.products.length ; i++)
      {
          if (this.props.products[i]['productId'] == this.props.user.cart[j]) {
              productsForUser.push(this.props.products[i]);
          }
      }
    }

    const cartProducts = productsForUser.map((product) =>
            <div className="item">
              <div className="buttons">
                <span className="delete-btn"></span>
              </div>

              <div className="image">
                <img className = 'imageStyle' src={product.imgSrc} alt=""/>
              </div>

              <div className="description">
                <span>{product.desc}</span>
              </div>

              <div className="quantity">
                <button
                  className="plus-btn"
                  type="button"
                  name="button"
                  onClick={() => this.addFunction('+')}>
                  +
                </button>

                <p>{this.state.value}</p>

                <button
                  className="minus-btn"
                  type="button"
                  name="button"
                  onClick={() => this.subFunction('-')}>
                  -
                </button>
              </div>

              <div className="total-price">{product.price}</div>
              </div>
            );

    return (

      <div className="shopping-cart text-center">
        <h3 className='title1'>
          שלום רב {this.props.user.name} זהו סל המוצרים שלך
        </h3>
        {cartProducts}
      </div>
    );
  }
}

export default Cart;
