import React from 'react';
import ReactDOM from 'react-dom';
import clothes from '../../../../img/clothes.jpg'
import students from '../../../../img/students.jpg'
import friends from '../../../../img/friends.jpg'
import soldure from '../../../../img/soldure.jpg'

class CarouselItem extends React.Component {
  render() {
    var src = this.props.item.src;
    src = src.slice(1, -1);

    return(
      <div class={this.props.item.cItem}>
        <img src={src} alt={this.props.item.imgAlt}/>
      </div>
    );
  }
}

export default CarouselItem
