import React from 'react';
import ReactDOM from 'react-dom';

class MenuItem extends React.Component {
  render() {
    var text = this.props.item.title;
    return(
      alert({text})
    );
      //normal right menu item
      if (this.props.item.level == 1)
      {
        return(
          <li className={this.props.item.cItem}>
            <a className={this.props.item.cLink} href={this.props.item.url}>
              {this.props.item.title}
            </a>
          </li>
        );
      }
      //dropdown menu item
      else if (this.props.item.level == 2)
      {
        const dropItems = this.props.item.drop_items.map((dropItem) =>
                          "<li><a >{dropItem}</a></li>");

        return(
          <li className={this.props.item.cItem}>
            <a className={this.props.item.cLink} data-toggle="dropdown" href="#">
              {this.props.item.title}
              <span className="caret"></span>
            </a>
            <ul className="dropdown-menu text-right pr-2">
              {dropItems}
            </ul>
          </li>
        );
      }
  }
}

export default MenuItem
