import React from 'react';
import ReactDOM from 'react-dom';
import './InfoCard.css'

class InfoCard extends React.Component {
  render() {
    return (
        <div id={this.props.id} class="col-12 col-md-7 ml-3 mt-2">
          <div class="infoTitle shadow-lg p-2 ">
            <h4>{this.props.title}</h4>
          </div>
          <div class="infoContent shadow-lg p-5">
            <span class="justify-content-center">
              {this.props.content}
            </span>
          </div>
        </div>
    )
  }
}

export default InfoCard;
