import React from 'react';
import ReactDOM from 'react-dom';
import Header from '../../parts/header/Header.js'
import Footer from '../../parts/footer/Footer.js'
import { PopupWidget } from "react-calendly";
import './Products.css'
import {ProductList} from './ProductList'//myJson
import ProdCard from '../../general/prodCard/ProdCard'

class Products extends React.Component {
  constructor(props)
  {
    super(props);
  }

  render() {
    const products = ProductList.map((singleItem) =>
                      <ProdCard item={singleItem}/>
                    );
    return (
      <div>
        <Header
          logoFlag = '1'
          withMyPic = '0'
          withLogo = '0'
          logoClass = 'headerProducts divProdBackImg row'
          pgTitle = 'קטלוג המוצרים שלנו'
          innerDivClass='galLogoInner'
         />

        <PopupWidget
           color="#00a2ff"
           text=" קבעי תור עכשיו"
           textColor="#ffffff"
           url="https://calendly.com/afraa-kablan/echg"
        />

        <div class="row">
          {products}
        </div>

        <Footer />
      </div>
    );
  }
}

export default Products;
