import React from 'react';
import ReactDOM from 'react-dom';
import InfoRight from './InfoRight'
import InfoLeft from './InfoLeft'

import nailsP from '../../../../img/nailsPic1.png'
import './Content.css';


class Content extends React.Component {
  render() {
    return (
      <div id="content" class="container mt-5">

        <InfoRight infoID='aboutUs' infoClass='row justify-content-center' id='info' class='col-6 mt-2' titleClass='title title2 shadow-lg p-3'
                  title='MaisAllure ' contentClass='infoContent shadow p-5 '
                  divImgClass='extra col-6 mt-2 pb-3 pl-3' imgClass='img-fluid rounded mt-4' imgAlt='nails' imgSrc={nailsP}
                  content='  סלון היופי והאסתטיקה MaisAllure הבית החם שלנו, קיים מאז 2015 ומציע מגוון רחב של שירותים.<br/> כל אחת מחברות הצוות בסלון הינה מוכשרת בעלת תעודה מוכרת בינלאומית. <br/> אנו נותנים שירותי קוסמטיקה מכל הלב. <br/>
                    כל הזמן ישנם חידושים בסלון , הוספת תחומים נוספים והשתלמויות לחיזוק והוספת ידע.'
                  />

      </div>
    );
  }
}

export default Content;
