import React from 'react';
import ReactDOM from 'react-dom';
import CardItem from './CardItem'
import './Cart.css'
import {evaluate} from 'mathjs'

class Cart extends React.Component {
  constructor(props)
  {
    super(props);
    this.state = {
      value: 1,
      total:100,
    }
    this.ref = React.createRef();
  }

  addFunction = (val) => {
    this.setState({value: this.state.value+ 1});
    this.setState({total: evaluate(this.state.val*100)});
  };
  subFunction = (val) => {
    this.setState({value: this.state.value- 1});
    (this.state.value == 0)?
      this.setState({value: 0}):
      console.log(val);
  }
  render() {
    var productsForUser = [];
    // alert(this.props.products[0]['productId']);
    console.log(this.props.user.cart[0]);
    for(var j=0; j<this.props.user.cart; j++){
      for (var i=0 ; i < this.props.products.length ; i++)
      {
          if (this.props.products[i]['productId'] == this.props.user.cart[j]) {
              productsForUser.push(this.props.products[i]);
          }
      }
    }
    console.log(productsForUser);

    const cartProducts = productsForUser.map((singleProduct) =>
            <CardItem value = {this.state.value} product = {singleProduct}/>
            );

    return (

      <div className="shopping-cart text-center">
        <h3 className='title1'>
          שלום רב {this.props.user.name} זהו סל המוצרים שלך
        </h3>
        <div className="item">
          <div className="buttons">
            <span className="delete-btn"></span>
          </div>

          <div className="image">
            <img className = 'imageStyle' src='img/30.png' alt=""/>
          </div>

          <div className="description">
            <span> 'לק בצבע אדום חזק יפה במיוחד מתאים לציפורניים שלך'</span>
          </div>

          <div className="quantity">
            <button
              className="plus-btn"
              type="button"
              name="button"
              onClick={() => this.addFunction('+')}>
              +
            </button>

            <p>{this.state.value}</p>

            <button
              className="minus-btn"
              type="button"
              name="button"
              onClick={() => this.subFunction('-')}>
              -
            </button>
          </div>

          <div className="total-price">{this.state.total}</div>
        </div>


      </div>
    );
  }
}

export default Cart;
