import React from 'react';
import ReactDOM from 'react-dom';
import InfoCard from '../../../general/InfoCard/InfoCard'

class Info extends React.Component {
  render() {
    return (
      <div id="content" class="container mt-5">
        <div class="aboutUs" class="row justify-content-center">
          <InfoCard id='info' class='col-6 mt-2' titleClass='title title2 shadow-lg p-3'
                    title='MaisAllure ' contentClass='infoContent shadow p-5 '
                    content='  סלון היופי והאסתטיקה MaisAllure הבית החם שלנו, קיים מאז 2015 ומציע מגוון רחב של שירותים.<br/> כל אחת מחברות הצוות בסלון הינה מוכשרת בעלת תעודה מוכרת בינלאומית. <br/> אנו נותנים שירותי קוסמטיקה מכל הלב. <br/>
                      כל הזמן ישנם חידושים בסלון , הוספת תחומים נוספים והשתלמויות לחיזוק והוספת ידע.'
                    />
          <div class="extra col-6 mt-2 pb-3 pl-3">
            <img src="img/nailsPic1.png" class="img-fluid rounded mt-4" alt="nails"/>
          </div>
        </div>
      </div>
    )
  }
}

export default Info;
