import React from 'react';
import ReactDOM from 'react-dom';
import './SignUpForm.css'

class FormSignup extends React.Component {
  constructor(props) {
       super(props);
       this.state = {
         isValid: false,
         username: '',
         email: '',
         password:'',
         password2: '',
         isSubmitBtnClicked: false,
         errors: {
          username: 'aaaaaaa',
          email: '',
          password: '',
          password2: '',
        }
       }
  }
  onNameChange(event) {
      this.setState({username: event.target.value});
  }
  onEmailChange(event) {
      this.setState({email: event.target.value});
  }
  onPasswordChange(event) {
      this.setState({password: event.target.value});
  }
  onPassword2Change(event) {
      this.setState({password2: event.target.value});
  }
  onSubmitClicked(event){
    this.setState({isSubmitBtnClicked: true});
  }

  validate = () => {
    let errors = this.state.errors;
    if (!this.state.name) {
      errors.name= "הכניסי שם משתמש";
    }
    if (!this.state.email){
      errors.email="הכניסי כתובת דואר אלקטרוני";
    }else if (/^[a-zA-Z0-9.!#$%&'*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(?:\.[a-zA-Z0-9-]+)*$/.test(this.state.email))
    {
      errors.email= "כתובת דואר אלקטרוני לא חוקית";
    }
    if (!this.state.password){
      errors.password= "הכניסי סיסמה";
    } else if (this.state.password.length < 6) {
      errors.password= "סיסמה לא חוקית, הכניסי לפחות 6 תוים";
    }
    if (!this.state.password2){
      errors.password= "הכניסי סיסמה";
    } else if (this.state.password2 !== this.state.password) {
      errors.password= "סיסמאות לא תואמות";
    }
    return true;
  };

  handleSubmit = event => {
    event.preventDefault();
    this.setState({isValid: this.validate()});
    if (this.state.isValid) {
      console.log(this.state);
      // clear form
      this.setState(this.setState({username: ''}));
      this.setState(this.setState({email: ''}));
      this.setState(this.setState({password: ''}));
      this.setState(this.setState({password2: ''}));
    }
  };

  render() {
      return (
      <div className='form-content-left col-md-6 text-center' onsubmit={this.handleSubmit}>
        <div className='welcome'>
          <h3>
            תצטרפנה ללקוחות הסלון המפונקות שלנו ותהנו ממגוון שירותים!
          </h3>
        </div>
        <div className='createMsg'>
          <h2>
            צרי חשבון עכשיו
          </h2>
        </div>
        <form className='form justify-content-center'>
          <div className='form-inputs'>
            <input
              id='username'
              type='text'
              name='username'
              className='form-input'
              placeholder='הכנס שם משתמש'
              value={this.state.username}
              onChange={this.onNameChange.bind(this)}
            />
            <p>{this.state.errors.username}</p>
          </div>
          <div className='form-inputs'>

            <input
              id='email'
              type='text'
              name='email'
              className='form-input'
              placeholder='הכנס כתובת דואר אלקטרוני'
              value={this.state.email}
              onChange={this.onEmailChange.bind(this)}
            />
            {this.state.errors.email && <p>{this.state.errors.email}</p>}
          </div>
          <div className='form-inputs'>
            <input
              id='password'
              type='password'
              name='password'
              className='form-input'
              placeholder='הכנס סיסמה'
              value={this.state.password}
              onChange={this.onPasswordChange.bind(this)}
            />
            {this.state.errors.password && <p>{this.state.errors.password}</p>}
          </div>
          <div className='form-inputs'>
            <input
              id='password2'
              type='password'
              name='password2'
              className='form-input'
              placeholder=' הכנס סיסמה לאימות'
              value={this.state.password2}
              onChange={this.onPassword2Change.bind(this)}
            />
            {this.state.errors.password2 && <p>{this.state.errors.password2}</p>}
          </div>
          <div className='form-inputs'>
            <button
                className='form-input-btn '
                type='submit'
                onClick={this.onSubmitClicked.bind(this)}>
                צרי חשבון
            </button>
          </div>

          <p className='form-input-login p-3'>
              יש לך חשבון קיים? לחץ
              <a href='#'> כאן </a>
              לכניסה
          </p>
        </form>
      </div>
    );
  }
}

export default FormSignup;
