import React from 'react';
import ReactDOM from 'react-dom';
import './SignUpForm.css'
import validateInfo from './ValidateInfo'

class FormSignup extends React.Component {
  constructor(props) {
       super(props);
       this.state = {
         username: '',
         email: '',
         password:'',
         password2: '',
         isSubmitBtnClicked: false,
         errors: {
          fullName: '',
          email: '',
          password: '',
          password2: '',
        }
       }
  }
  onNameChange(event) {
      this.setState({username: event.target.value});
  }
  onEmailChange(event) {
      this.setState({email: event.target.value});
  }
  onPasswordChange(event) {
      this.setState({password: event.target.value});
  }
  onPassword2Change(event) {
      this.setState({password2: event.target.value});
  }
  onSubmitClicked(event){
    this.setState({isSubmitBtnClicked: true});
  }
  handleChange = (event) => {
    event.preventDefault();
    let name = event.target.name;
    let value = event.target.value;
    let errors = this.state.errors;

    const validEmailRegex =
      RegExp(/^(([^<>()\[\]\.,;:\s@\"]+(\.[^<>()\[\]\.,;:\s@\"]+)*)|(\".+\"))@(([^<>()[\]\.,;:\s@\"]+\.)+[^<>()[\]\.,;:\s@\"]{2,})$/i);
    switch (name) {
        case 'fullName':
          errors.fullName =
            value.length < 5
              ? 'Full Name must be 5 characters long!'
              : '';
          break;
        case 'email':
          errors.email =
            validEmailRegex.test(value)
              ? ''
              : 'Email is not valid!';
          break;
        case 'password':
          errors.password =
            value.length < 8
              ? 'Password must be 8 characters long!'
              : '';
          break;
        default:
          break;
      }

      this.setState({errors, [name]: value}, ()=> {
          console.log(errors)
      })
  }
  render() {
      return (
      <div className='form-content-left col-md-6 text-center'>
        <div className='welcome'>
          <h3>
            תצטרפנה ללקוחות הסלון המפונקות שלנו ותהנו ממגוון שירותים!
          </h3>
        </div>
        <div className='createMsg'>
          <h2>
            צרי חשבון עכשיו
          </h2>
        </div>
        <form className='form justify-content-center'>
          <div className='form-inputs'>
            <input
              id='username'
              type='text'
              name='username'
              className='form-input'
              placeholder='הכנס שם משתמש'
              value={this.state.username}
              onChange={this.onNameChange.bind(this)}
            />
            {this.state.errors.username && <p>{this.state.errors.username}</p>}
          </div>
          <div className='form-inputs'>

            <input
              id='email'
              type='text'
              name='email'
              className='form-input'
              placeholder='הכנס כתובת דואר אלקטרוני'
              value={this.state.email}
              onChange={this.onEmailChange.bind(this)}
            />
            <p></p>
          </div>
          <div className='form-inputs'>
            <input
              id='password'
              type='password'
              name='password'
              className='form-input'
              placeholder='הכנס סיסמה'
              value={this.state.password}
              onChange={this.onPasswordChange.bind(this)}
            />
            <p></p>
          </div>
          <div className='form-inputs'>
            <input
              id='password2'
              type='password'
              name='password2'
              className='form-input'
              placeholder=' הכנס סיסמה לאימות'
              value={this.state.password2}
              onChange={this.onPassword2Change.bind(this)}
            />
            <p></p>
          </div>
          <div className='form-inputs'>
            <button
                className='form-input-btn '
                type='submit'
                onClick={this.onSubmitClicked.bind(this)}>
                צרי חשבון
            </button>
          </div>

          <p className='form-input-login p-3'>
              יש לך חשבון קיים? לחץ
              <a href='#'> כאן </a>
              לכניסה
          </p>
        </form>
      </div>
    );
  }
}

export default FormSignup;
