import React from 'react';
import ReactDOM from 'react-dom';
import './Cart.css'

class Cart extends React.Component {
  constructor(props)
  {
    super(props);
    this.state = {
      value: 1,
    }
    this.ref = React.createRef();
  }


  addFunction = (val) => {
      console.log(val);
  };
  subFunction = (val) => {
    console.log(val);
  }
  render() {
    return (
      <div className="shopping-cart text-center">
        <h3 className='title1'>
          שלום רב {this.props.user.name} זהו סל המוצרים שלך
        </h3>

        <div className="item">
          <div className="buttons">
            <span className="delete-btn"></span>
          </div>

          <div className="image">
            <img className = 'imageStyle' src='/img/fashion-facts.jpg' alt=""/>
          </div>

          <div className="description">
            <span>Maison Margiela sddasdad</span>
          </div>

          <div className="quantity">
            <button
              className="plus-btn"
              type="button"
              name="button"
              onClick={() => this.addFunction('+')}>
              +
            </button>

            <input
              type="text"
              name="name"
              value={this.state.value}
              ref={this.ref}/>

            <button
              className="minus-btn"
              type="button"
              name="button"
              onClick={() => this.subFunction('-')}>
              -
            </button>
          </div>

          <div className="total-price">$870</div>
        </div>

      </div>
    );
  }
}

export default Cart;
