import React from 'react'
import ReactDOM from 'react-dom';
import './FormGo2SignUp.css'
import { Redirect } from 'react-router-dom'
import SignUp from '../../signUp.SignUp'
class FormGo2SignUp extends React.Component {
  constructor(props) {
       super(props);
       this.state = {
         isBtnClicked: false
       };
  }
  onBtnClicked(event) {
      this.setState({isBtnClicked: !this.state.isBtnClicked})
  }
  render() {
    if(this.state.isBtnClicked){
      <Redirect to='/signUp'/>
    }
      return (
      <div className='form-content-right col-md-6 text-center'>
        <div className='form-signin'>
          <h1 className='welcome1'> ברוכה הבאה! </h1>
          <p className='msg'>תצטרפי למועדון הלקוחות שלנו, ותהני מהטבות!</p>

          <button
              className='sign-in-btn'
              type='button'
              onClick={this.onBtnClicked.bind(this)}>
              התחברות
          </button>
        </div>
      </div>
    );
  }
}

export default FormGo2SignUp;
