import React from 'react';
import ReactDOM from 'react-dom';

class CarouselItem extends React.Component {
  constructor(props)
  {
    super(props);
  }

  render() {
    return(
      <div className={'item ${this.props.carouselItem.itemClass}'}>
        <img src={this.props.carouselItem.src}
             alt={this.props.carouselItem.imgAlt}/>
        <div className='caption'>
          <h3>{this.props.carouselItem.captionTitle}</h3>
          <p>{this.props.carouselItem.captionContent}</p>
        </div>
      </div>
    );
  }
}

export default CarouselItem
